import React, { Component } from "react";
import Personalchild from "./Personalchild";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser,faEnvelope,faPhone,faHome,faWarehouse,} from '@fortawesome/fontawesome-free-solid'

class Cv extends Component{
    constructor(){
        super()
        this.state={
            info :[
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).firstName} ${JSON.parse(localStorage.getItem("Personal Data")).lastName}`,info:"Name",icon:faUser},
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).email}`,info:"Email",icon:faEnvelope},
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).phoneNumber}`,info:"Phone Numper",icon:faPhone},
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).Address}`,info:"Address",icon:faHome},
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).city}`,info:"city",icon:faWarehouse},
          {data:`${JSON.parse(localStorage.getItem("Personal Data")).zibCode}`,info:"Zip Code",icon:faWarehouse}



          

                
        
            ]
        }
       

    }
    componentDidMount(){
        console.log(JSON.parse(localStorage.getItem("Personal Data")).img)
    }
    render(){
        const skill = this.state.info.map(function(xx){
         if(xx.data !==""&&xx.data!==" "){
            return <Personalchild 
            info={xx.info}
            data={xx.data}
            icon = {xx.icon}
           
            />
         }
         })

 
        return(
            <div className="cv-parent">
                <div className="container">
                    <div className="row">
                        <div className="col-md-3 cv-personan-parent">
<div className="cv-personal-parent-style"></div>
<div className="data-container">
<h3>{`${JSON.parse(localStorage.getItem("Personal Data")).firstName} ${JSON.parse(localStorage.getItem("Personal Data")).lastName}`}</h3>
<div className="personal-image-container">
<img src={JSON.parse(localStorage.getItem("Personal Data")).img}/>
<h5>Personal Data</h5>
{skill}
</div>
</div>
                        </div>
                        <div className="col-md-9 cv-techincal-parent">

                        </div>
                    </div>
                </div>
                
            </div>
        )
    }

}
export default Cv
