import { Component } from "react"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faKeyboard,faPencilAlt} from '@fortawesome/fontawesome-free-solid'
import Skill from "./Skill";



class Skills extends Component{
    constructor(){
        super()
        this.state={
            info :[
            
                {skill:"PS",level:"advanced",id:"123123"},
                {skill:"PS",level:"advanced",id:"12354123"},
                {skill:"PS",level:"advanced",id:"153123"},
                {skill:"PS",level:"advanced",id:"124g23"},
              
            ]
        }

    }
    componentDidMount(){
let Skills = document.getElementById("Skills");
let SkillsWindow = document.getElementById("Skills-editor");
let savebutton = document.getElementById("save");
let select = document.querySelector("#select")
let skillsArray=[];
savebutton.onclick =function(){
    let object = {skill:"",level:"",}
   object.level = select.value
   object.skill = document.querySelector("#skill-name").value;
   skillsArray.push(object)
   localStorage.setItem("skills",JSON.stringify(this.state.info))
   console.log(this.state.info)
   
}

function open(prameter){
    prameter.classList.toggle("show")
}
Skills.onclick =function(){ Skills.addEventListener("click",open(SkillsWindow))
}
  

}
 
render(){
    const skill = this.state.info.map(function(xx){
        return <Skill 
        skill={xx.skill}
        level = {xx.level}
       
        />
     })



    return(   
        <div style={{justifyContent:"center",display:"flex",marginTop:"3rem"}}>
       <div className="col-sm-10 Skills-container" >

       <span style={{position:"relative"}}><FontAwesomeIcon icon={faKeyboard} style={{position:"absolute",fontSize:"1.4rem"}}/> <span id="counter">1</span></span> 
       <h5 id="Skills">Skills </h5>

       <div >
        <div id="Skills-editor" className="Skills-editor">

            <hr></hr>

  {skill}
        <div style={{display:"flex",justifyContent:"space-around"}} className="label-skill">
        <label className="col-sm-5">Skill</label>
        <label className="col-sm-5">Level</label>
        </div>
        <div style={{display:"flex",justifyContent:"space-around"}}>
        <input type="text" placeholder="e.g Micorsoft Word" className="skill-name col-sm-5" id="skill-name"/>
        <select className="skill-name col-sm-5" style={{cursor:"pointer"}} id="select">
    <option value="beginner">Beginner</option>
    <option value="intermediate">Intermediate</option>
    <option value="skillful">Skillful</option>
    <option value="experienced">Experienced</option>
    <option value="expert">Expert</option>

    expert
  </select>
        </div>
        <div style={{display:"flex",justifyContent:"end",marginTop:"1em"}} className="skills-option-button">
        <button id="save">save</button>
        </div>
        </div>
       </div>
       </div>
      </div>
      )
        
    
}
}


export default Skills
