    try {
      const response = await axios.get('https://restcountries.com/v3.1/all');
      return response;
    } catch (error) {
    
    }
  }
class Countries extends Component{
    state={
        countries:[],
        filttred:"",
        
    }
  
componentDidMount(){
  alert("good")
}
  
  


    render(){
      let input = document.getElementById("input")
      const filltered = this.state.countries.filter(function(i){

        return i.region==input.value
        console.log(input)
      })
        const country = filltered.map(function(i){

            return <Country 
             img={i.flags.png}
           name={i.name.common}
            pop = {i.population}
             reg = {i.region}
          cap = {i.capital}
           
            />
           
         
        
         })
         


        
        return(
            <div className="countries">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-8 search-bar" >
                            <input className="col-sm-8 search-input" type="Text" placeholder="Search for a country"  id="input" value="Africa"
                          
                          />
                        </div>
                    </div>
                    <div className="row">
                      {country}
           {console.log(filltered)}
                    </div>
                </div>
                
            </div>
        )
    }
}
export default Countries
