import { Component } from "react"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPalette,faFile,faPencilAlt,faCamera,faAngleRight} from '@fortawesome/fontawesome-free-solid'



class Interstes extends Component{
constructor(){
    super()
    this.state ={
        info:[

        ]
    }
}
save(){
    let select = document.querySelector("#select")
    let skill = document.querySelector("#skill-name")
    if(skill.value !==""&&skill.value!==" "){

    this.setState({info:[...this.state.info,{skill:skill.value,level:select.value}]})
    }
   
 }
    componentDidMount(){
let resume = document.getElementById("inter");
let resumeWindow = document.getElementById("inter-editor")

function open(prameter){
    prameter.classList.toggle("show")
}
resume.onclick =function(){ resume.addEventListener("click",open(resumeWindow))
}
    }
 
render(){
    const hobby = this.state.info.map(function(xx){
        return <Skill 
        skill={xx.skill}
        level = {xx.level}
        
       
        />
     })



    return(   
        <div style={{justifyContent:"center",display:"flex",marginTop:"3rem"}}>
       <div className="col-sm-10 resume-container" >

       <span style={{position:"relative"}}><FontAwesomeIcon icon={faPalette} /> <span id="counter">1</span></span> 
       <h5 id="inter">Interests</h5>
    
       <div >
        <div  id="inter-editor" className="Resume-editor">
        <hr></hr>
        {hobby}
            <h6>Hobby</h6>
            <input type="text" placeholder="e.g Drawing" className="skill-name col-sm-12" id="hobby-name"/>
            <button id="savehobby" onClick={()=> this.save()}>save</button>



        </div>
       </div>
       </div>
      </div>
      )
        
    
}
}


export default Interstes
