import { Component } from "react";
import Country from "./Country";
import axios from 'axios';

async function getUsers() {
    try {
      const response = await axios.get('https://restcountries.com/v3.1/all');
      return response;
    } catch (error) {
    
    }
  }
class Countries extends Component{
    state={
        countries:[],
        
    }
  
 
  
    componentDidMount =()=>{
        getUsers().then(response => {
          this.setState({
            countries: response.data
          });
        });
       
     
    
      }
  


    render(){
        const country = this.state.countries.map(function(i){
           
            return <Country 
            img={i.flags.png}
            name={i.name.common}
            pop = {i.population}
            reg = {i.region}
            cap = {i.capital}
           
            />
          
      
         })

         const filttred = this.state.countries.filter(function(country){
return country.name.common==document.querySelector("#test").value
         })
        
        return(
            <div className="countries">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-8 search-bar" >
                            <input className="col-sm-8 search-input" type="Text" placeholder="Search for a country" id="test" 
                            onChange={function(e){
                                console.log(this.state.countries)
                            }}
                          />
                        </div>
                    </div>
                    <div className="row">
                        {country}
                    </div>
                </div>
                
            </div>
        )
    }
}
export default Countries
