import { Component } from "react"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faGlobe} from '@fortawesome/fontawesome-free-solid'
import Language from "./Language"




class Languages extends Component{
    constructor(){
        super()
        this.state={
            info :[
          
                
        
            ]
        }
       

    }
    save(){
        let select = document.getElementById("selectLang")
        let language = document.querySelector("#language-name")
      
if(language.value!==""&&language.value!==" "){
        this.setState({info:[...this.state.info,{language:language.value,level:select.value}]})
    } 
       
     }
    componentDidMount(){

let languagewindow = document.getElementById("languagewindow");
let language = document.getElementById("language")
function open(prameter){
    prameter.classList.toggle("show")
}

language.onclick =function(){ language.addEventListener("click",open(languagewindow))
}
//--------------------------

buttonsave.onclick = function(){
   

    let obj = {skill:"",level:""}
obj.level = select.value;
obj.skill = skill.value
arr.push(obj)
localStorage.setItem("skills",JSON.stringify(arr))
    
}

}


 
render(){
    const skill = this.state.info.map(function(xx){
        return <Language 
        skill={xx.language}
        level = {xx.level}
      
       
        />
     })

  


    return(   
        <div style={{justifyContent:"center",display:"flex",marginTop:"3rem"}}>
       <div className="col-sm-10 Skills-container" >

       <span style={{position:"relative"}}><FontAwesomeIcon icon={faGlobe} style={{position:"absolute",fontSize:"1.4rem"}}/> <span id="counter">1</span></span> 
       <h5 id="language">Languages </h5>

       <div >
        <div id="languagewindow" className="Skills-editor">

            <hr></hr>

  {skill}
        <div style={{display:"flex",justifyContent:"space-around"}} className="label-skill">
        <label className="col-sm-5">Language</label>
        <label className="col-sm-5">Level</label>
        </div>
        <div style={{display:"flex",justifyContent:"space-around"}}>
        <input type="text" placeholder="e.g English" className="skill-name col-sm-5" id="language-name"/>
        <select className="language-name col-sm-5" style={{cursor:"pointer"}} id="selectLang">
    <option value="Native speaker">Native speaker</option>
    <option value="Highly proficient in speaking and writing">Highly proficient in speaking and writing</option>
    <option value="Very good command">Very good command</option>
    <option value="Good working knowledge">Good working knowledge</option>
    <option value="Working knowledge">Working knowledge</option>

    expert
  </select>
        </div>
        <div style={{display:"flex",justifyContent:"end",marginTop:"1em"}} className="skills-option-button">
        <button id="savelang" onClick={()=> this.save()}>save</button>
        </div>
        </div>
       </div>
       </div>
      </div>
      )
        
    
}
}


export default Languages
